		int intMax= Integer.MAX_VALUE;
		int intMin = Integer.MIN_VALUE;
		
		byte byteMin = Byte.MIN_VALUE;
		byte byteMax = Byte.MAX_VALUE;
		
		float floatMin = Float.MAX_VALUE;
		float floatMax = Float.MIN_VALUE;
		System.out.println("Ýnt Max Degeter: " + intMax);
		System.out.println("int Min Deger: " + intMin);
		
		System.out.println("byte Min Deger: " + byteMin +"\n"
							+ "byte Max Deger: " + byteMax);
		
		System.out.println("float Min Deger: " + floatMin +"\n"
				+ "float Max Deger: " + floatMax);
		

	
	
	
	
	
	
	
	
	
